// import { useState } from "react"
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";



import HomePage from "./HomePage";
import Favourites from "./Favourites";
import './App.css'
import Facts from "./Facts";
import Slides from "./Slides";



function App() {

  //STEP 2
  //Вместо <div></div> мы используем тэг <nav></nav> и внутри вместо тега  <a></a> как мы раньше испольовали в HTML мы теперт будем использовать <Link to></Link>
  return (
    <>
      <Router>
        <nav>
          <Link to="/" className="link">Home page</Link>
          <Link to="/favourites" className="link">Favourites</Link>
          <Link to="/facts" className="link">Facts</Link>
        </nav>


        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/favourites" element={<Favourites />} />
          <Route path="/facts" element={<Facts />} />
        </Routes>
      </Router>


      <div className="circleContainer">
        <div className="oneCircle">
          <svg xmlns="http://www.w3.org/2000/svg" width="132" height="132" viewBox="0 0 132 132" fill="none">
            <path fill-rule="evenodd" clip-rule="evenodd" d="M66 11.7857C36.0583 11.7857 11.7857 36.0583 11.7857 66C11.7857 95.9417 36.0583 120.214 66 120.214C95.9417 120.214 120.214 95.9417 120.214 66C120.214 36.0583 95.9417 11.7857 66 11.7857ZM0 66C0 29.5492 29.5492 0 66 0C102.451 0 132 29.5492 132 66C132 102.451 102.451 132 66 132C29.5492 132 0 102.451 0 66Z" fill="#223D64" />
          </svg>

          <svg xmlns="http://www.w3.org/2000/svg" width="132" height="132" viewBox="0 0 132 132" fill="none">
            <path d="M66 132C102.451 132 132 102.451 132 66C132 29.5492 102.451 0 66 0C29.5492 0 0 29.5492 0 66C0 102.451 29.5492 132 66 132Z" fill="#1A2E4B" />
          </svg>
        </div>

      </div>
    </>
  )
}

export default App;
