// import { useState } from "react"
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink
} from "react-router-dom";


import { useState } from 'react';
import HomePage from "./HomePage";
import Favourites from "./Favourites";
import './App.css'
import DidYouKnow from "./DidYouKnow";




function App() {
  const [bookList, setBookList] = useState([]);

  //STEP 2
  //Вместо <div></div> мы используем тэг <nav></nav> и внутри вместо тега  <a></a> как мы раньше испольовали в HTML мы теперт будем использовать <Link to></Link>
  return (
    <>


      <Router>
        <nav>
        /

          <svg className="item start"
  xmlns="http://www.w3.org/2000/svg"
  width="72"
  height="72"
  viewBox="0 0 56 56"
  fill="none">
  <path d="..." fill="#CDE7BE" />
</svg>

          <NavLink to="/" className={({ isActive }) => "item body-text-18" + (isActive ? " active-nav" : "")}>Home page</NavLink>
          <NavLink to="/favourites" className={({ isActive }) => "item body-text-18" + (isActive ? " active-nav" : "")}>Favourites</NavLink>
          <NavLink to="/DidYouKnow" className={({ isActive }) => "item body-text-18" + (isActive ? " active-nav" : "")}>Did you now?</NavLink>
        </nav>


        <Routes>
          <Route path="/" element={<HomePage bookList={bookList} setBookList={setBookList} />} />
          <Route path="/favourites" element={<Favourites bookList={bookList} />} />
          <Route path="/DidYouKnow" element={<DidYouKnow />} />
        </Routes>
      </Router>


    </>

  )
}

export default App;
