import { useState } from "react"


export const AccordionItem = ({ image, author, content }) => {
    const [isOpen, setIsOpen] = useState(false)
    const handleIsopen = () => {
        setIsOpen(!isOpen)
    }


    return (
        <div>

            <div className="image-content-container">

                <img className="image-accordion"
                    src={image}
                    alt={author}
                />
                <div>
                    <div className={isOpen ? 'active' : 'default'} onClick={handleIsopen}>

                        <div className="author-icon">
                            <div className="author title-4">{author}</div>
                            {isOpen ? (
                                <svg className xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none">
                                    <path fill-rule="evenodd" clip-rule="evenodd" d="M3.50156 7.61375C4.21803 6.84752 5.43775 6.79105 6.22588 7.48763L12 12.591L17.7741 7.48763C18.5622 6.79105 19.782 6.84752 20.4984 7.61375C21.2149 8.37998 21.1568 9.56582 20.3687 10.2624L13.2973 16.5124C12.5617 17.1625 11.4383 17.1625 10.7027 16.5124L3.63129 10.2624C2.84316 9.56582 2.78508 8.37998 3.50156 7.61375Z" fill="#1A2E4B" />
                                </svg>
                            ) : (
                                <svg className="icon" xmlns="http://www.w3.org/2000/svg"
                                    width="24"
                                    height="24"
                                    viewBox="0 0 24 24" fill="none">
                                    <g transform="translate(2,2) scale(1.3)">
                                        <path fillRule="evenodd" clipRule="evenodd" d="M1.3901 4.49099C1.94736 3.878 2.89603 3.83283 3.50902 4.39009L8.00001 8.47281L12.491 4.39009C13.104 3.83283 14.0527 3.878 14.6099 4.49099C15.1672 5.10398 15.122 6.05265 14.509 6.60991L9.00902 11.6099C8.43689 12.13 7.56313 12.13 6.991 11.6099L1.491 6.60991C0.878015 6.05265 0.83284 5.10398 1.3901 4.49099Z" fill="#181A1A" />
                                    </g>
                                </svg>
                            )}
                        </div>
                    </div>
                    {
                        isOpen && <div className="content secondary-text body-text-18">
                            {content}
                        </div>
                    }
                </div>
            </div>



        </div>
    )
}
