import { data } from 'react-router-dom';
import './App.css'
import Slides from "./Slides";
import { useEffect, useState } from 'react';


function HomePage() {

  const [books, setBooks] = useState([]);
  const [wordSubmitted, setWordSubmitted] = useState('');
  const [mySearch, setMySearch] = useState("");
  // const [stateLoader, setStateLoader] = useState(false);

  use
  const getData = async (searchWord) => {
    const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchWord}&maxResults=12`);

  //   if (response.ok) {
  //     setStateLoader(false);
  //     const data = await response.json();
  //     console.log("API response:", data);
  //     setBooks(data);
  //   } else {
  //     setStateLoader(false);
  //     // alert('ingredients entered incorrectly');
  //     Swal.fire({
  //       html: `
  //   <div style="display: flex; flex-direction: column; align-items: center;">
  //     <svg width="48" height="48" fill="red" viewBox="0 0 24 24"><path d="..."/></svg>
  //     <h2>double-check your ingredients</h2>
  //     <p>Enter ingredients in a format, like: 1 cup rice, 1 egg</p>
  //   </div>
  // `
  //     });
  //   }
  setBooks(data.book)
  }

  const bookSearch = (e) => {
    setMySearch(e.target.value)
  }

  const finalSearch = (e) => {
    e.preventDefault();
    setWordSubmitted(mySearch)
  }

  useEffect(() => {
    if (wordSubmitted !== '') {
      getData(wordSubmitted);
    }
  }, [wordSubmitted])


  return (<div>
    {/* {stateLoader && <LoaderPage />} */}

    <div className='search-books-wrapper'>
      <h1>Book Finder</h1>


      <p className="description-text">Search for your favorite books by title or author.</p>
      <form onSubmit={finalSearch}>
        <input
          className="inputField"
          onChange={bookSearch}
          value={mySearch}
          placeholder="Search for books..."
        />
        <button>SEARCH</button>
      </form>

      {/* render books */}
      <div className="books-grid">
          {books.map((book) => (
            <div key={book.id} className="book-card">
              <img
                src={book.volumeInfo.imageLinks?.thumbnail}
                alt={book.volumeInfo.title}
              />
              <h3>{book.volumeInfo.title}</h3>
              <p>{book.volumeInfo.authors?.join(", ")}</p>
              <p>{book.volumeInfo.publishedDate}</p>
            </div>
          ))}
        </div>


    </div>

    <Slides />
  </div>
  )
}


export default HomePage;
