import './App.css'
import Slides from "./Slides";



function HomePage() {

  const MY_ID = "03ae5e64";
  const MY_KEY = "43381e6503ee760589c2d3bc8ff0a43a";

  const [nutrition, setNutrition] = useState();
  const [wordSubmitted, setWordSubmitted] = useState('');
  const [mySearch, setMySearch] = useState("");
  const [stateLoader, setStateLoader] = useState(false);

  const getData = async (ingredients) => {
    const response = await fetch(`https://api.edamam.com/api/nutrition-details?app_id=${MY_ID}&app_key=${MY_KEY}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8'
      },
      body: JSON.stringify({
        title: "My Recipe",
        ingr: ingredients
      })
    })

    if (response.ok) {
      setStateLoader(false);
      const data = await response.json();
      setNutrition(data);
    } else {
      setStateLoader(false);
      // alert('ingredients entered incorrectly');
      Swal.fire({
        html: `
    <div style="display: flex; flex-direction: column; align-items: center;">
      <svg width="48" height="48" fill="red" viewBox="0 0 24 24"><path d="..."/></svg>
      <h2>double-check your ingredients</h2>
      <p>Enter ingredients in a format, like: 1 cup rice, 1 egg</p>
    </div>
  `
      });
    }
  }

  const recipeSearch = (e) => {
    setMySearch(e.target.value)
  }

  const finalSearch = (e) => {
    e.preventDefault();
    setWordSubmitted(mySearch)
  }

  useEffect(() => {
    if (wordSubmitted !== '') {
      let ingredients = wordSubmitted.split(/[\n,;]/);
      // /[\n,;]/ - регулярное выражение, чтобы разбить строку по , ; и перенос строки на новую строчку
      getData(ingredients);
    }
  }, [wordSubmitted])


  return (
    <div>
      <input type="text" placeholder="Search book..." />
      <p></p>
      <Slides />
    </div>
  )
}


export default HomePage;
