// import { useState } from "react"
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";



import HomePage from "./HomePage";
import Favourites from "./Favourites";
import './App.css'
import Facts from "./Facts";
import Slides from "./Slides";



function App() {

  //STEP 2
  //Вместо <div></div> мы используем тэг <nav></nav> и внутри вместо тега  <a></a> как мы раньше испольовали в HTML мы теперт будем использовать <Link to></Link>
  return (
    <>

    <input/ >
      <Router>
        <nav>
          <Link to="/" className="link">Home page</Link>
          <Link to="/favourites" className="link">Favourites</Link>
          <Link to="/facts" className="link">Facts</Link>
        </nav>


        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/favourites" element={<Favourites />} />
          <Route path="/facts" element={<Facts />} />
        </Routes>
      </Router>

<Slides/>
    </>
  )
}

export default App;
