// import { useState } from "react"
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";



import HomePage from "./HomePage";
import Favourites from "./Favourites";
import './App.css'
import Facts from "./Facts";




function App() {
  const MY_ID = "03ae5e64";
  const MY_KEY = "43381e6503ee760589c2d3bc8ff0a43a";

  const [books, setBooks] = useState();
  const [wordSubmitted, setWordSubmitted] = useState('');
  const [mySearch, setMySearch] = useState("");
  const [stateLoader, setStateLoader] = useState(false);

  const getData = async (book) => {
    const response = await fetch(`https://api.edamam.com/api/nutrition-details?app_id=${MY_ID}&app_key=${MY_KEY}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8'
      },
      body: JSON.stringify({
        title: "My Recipe",
        ingr: ingredients
      })
    })

    if (response.ok) {
      setStateLoader(false);
      const data = await response.json();
      setNutrition(data);
    } else {
      setStateLoader(false);
      // alert('ingredients entered incorrectly');
      Swal.fire({
        html: `
    <div style="display: flex; flex-direction: column; align-items: center;">
      <svg width="48" height="48" fill="red" viewBox="0 0 24 24"><path d="..."/></svg>
      <h2>double-check your ingredients</h2>
      <p>Enter ingredients in a format, like: 1 cup rice, 1 egg</p>
    </div>
  `
      });
    }
  }

  const recipeSearch = (e) => {
    setMySearch(e.target.value)
  }

  const finalSearch = (e) => {
    e.preventDefault();
    setWordSubmitted(mySearch)
  }

  useEffect(() => {
    if (wordSubmitted !== '') {
      let ingredients = wordSubmitted.split(/[\n,;]/);
      // /[\n,;]/ - регулярное выражение, чтобы разбить строку по , ; и перенос строки на новую строчку
      getData(ingredients);
    }
  }, [wordSubmitted])

  return (
    <>


      <Router>
        <nav>
          <Link to="/" className="link">Home page</Link>
          <Link to="/favourites" className="link">Favourites</Link>
          <Link to="/facts" className="link">Facts</Link>
        </nav>


        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/favourites" element={<Favourites />} />
          <Route path="/facts" element={<Facts />} />
        </Routes>
      </Router>

      {stateLoader && <LoaderPage />}

      <div className='bowlSearch'>
        <img src={bowl} alt="Bowl" />
        <div className="search-recipe-wrapper">
          <h1>What's in my bowl?</h1>
          <p className="description-text">
            Enter your recipe ingredients below to instantly get a detailed nutritional breakdown — calories, vitamins, minerals, and more.<br />
            Example: <em>1 egg, 1 avocado, 2 cups spinach</em>
          </p>


          <form onSubmit={finalSearch}>
            <input className='inputField' onChange={recipeSearch} value={mySearch} />
            <button>SEARCH</button>
          </form>



          {nutrition && (
            <IngredientsComponent
              calories={nutrition.calories}
              ingredients={nutrition.ingredientLines}
              totalNutrients={nutrition.totalNutrients}
              label="My Recipe"
            />
          )}
        </div>
      </div>



    </>
  )
}

export default App;
